import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      senha: "",
      mensagem: "",
    };
  }

  handleEmailChange = (event) => {
    this.setState({ email: event.target.value });
  };

  handleSenhaChange = (event) => {
    this.setState({ senha: event.target.value });
  };

  handleAcessar = () => {
    const { email, senha } = this.state;
    if (email === "joao.sander@pucpr.edu.br" && senha === "123456") {
      this.setState({ mensagem: "Acessado com sucesso!" });
    } else {
      this.setState({ mensagem: "Usuário ou senha incorretos!" });
    }
  };

  render() {
    return (
      <div className="App">
        <h1>Login</h1>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={this.state.email}
            onChange={this.handleEmailChange}
          />
        </div>
        <div>
          <label>Senha:</label>
          <input
            type="password"
            value={this.state.senha}
            onChange={this.handleSenhaChange}
          />
        </div>
        <button onClick={this.handleAcessar}>Acessar</button>
        <label>{this.state.mensagem}</label>
      </div>
    );
  }
}

export default App;
